section.screen:

$ cat test.php
#!/usr/bin/php
<?php

$handle = fopen("php://stdin", "r");

while (!feof($handle)) {
    $buffer = fgets($handle);
    print($buffer);
}
fclose($handle);


$ cat exception1.php
<?php

function test1(){
	throw new Exception("Test1 exception!");
}

try {
	test1();
}
catch(Exception $e){
	die( $e->__toString() );
}


